import React, { useState, useEffect } from 'react';
import { universityAPI, courseAPI } from '../services/api';
import {
  Box, Grid, Card, CardContent, Typography, Paper, Divider,
  Table, TableBody, TableCell, TableContainer, TableHead, TableRow
} from '@mui/material';
import {
  School, MenuBook, People, AttachMoney
} from '@mui/icons-material';

const Dashboard = () => {
  const [stats, setStats] = useState({
    totalUniversities: 0,
    totalCourses: 0,
    activeCourses: 0,
    totalRevenue: 0
  });
  const [recentCourses, setRecentCourses] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      
      // Fetch universities
      const universitiesRes = await universityAPI.getAllUniversities();
      const universities = universitiesRes.data;
      
      // Fetch all courses
      const coursesRes = await courseAPI.getAllCourses();
      const courses = coursesRes.data;
      
      // Fetch active courses
      const activeCoursesRes = await courseAPI.getActiveCourses();
      const activeCourses = activeCoursesRes.data;
      
      // Calculate total revenue
      const totalRevenue = courses.reduce((sum, course) => 
        sum + (parseFloat(course.tuitionFee) || 0), 0
      );
      
      setStats({
        totalUniversities: universities.length,
        totalCourses: courses.length,
        activeCourses: activeCourses.length,
        totalRevenue: totalRevenue
      });
      
      // Get recent courses (last 5)
      setRecentCourses(courses.slice(-5).reverse());
      
    } catch (err) {
      console.error('Failed to fetch dashboard data', err);
    } finally {
      setLoading(false);
    }
  };

  const StatCard = ({ icon: Icon, title, value, color }) => (
    <Card>
      <CardContent>
        <Box display="flex" alignItems="center" mb={2}>
          <Icon sx={{ color, fontSize: 40, mr: 2 }} />
          <Box>
            <Typography color="textSecondary" variant="body2">
              {title}
            </Typography>
            <Typography variant="h4">
              {title.includes('Revenue') ? `$${value.toFixed(2)}` : value}
            </Typography>
          </Box>
        </Box>
      </CardContent>
    </Card>
  );

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="400px">
        <Typography>Loading dashboard...</Typography>
      </Box>
    );
  }

  return (
    <Box>
      <Typography variant="h4" gutterBottom>
        University Management Dashboard
      </Typography>
      
      <Grid container spacing={3} mb={4}>
        <Grid item xs={12} sm={6} md={3}>
          <StatCard
            icon={School}
            title="Total Universities"
            value={stats.totalUniversities}
            color="#3f51b5"
          />
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <StatCard
            icon={MenuBook}
            title="Total Courses"
            value={stats.totalCourses}
            color="#f50057"
          />
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <StatCard
            icon={People}
            title="Active Courses"
            value={stats.activeCourses}
            color="#4caf50"
          />
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <StatCard
            icon={AttachMoney}
            title="Total Revenue"
            value={stats.totalRevenue}
            color="#ff9800"
          />
        </Grid>
      </Grid>
      
      <Grid container spacing={3}>
        <Grid item xs={12} md={6}>
          <Paper sx={{ p: 2 }}>
            <Typography variant="h6" gutterBottom>
              Recent Courses
            </Typography>
            <Divider sx={{ mb: 2 }} />
            <TableContainer>
              <Table size="small">
                <TableHead>
                  <TableRow>
                    <TableCell>Code</TableCell>
                    <TableCell>Title</TableCell>
                    <TableCell>Department</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {recentCourses.map((course) => (
                    <TableRow key={course.id}>
                      <TableCell>{course.courseCode}</TableCell>
                      <TableCell>{course.title}</TableCell>
                      <TableCell>{course.department}</TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>
          </Paper>
        </Grid>
        
        <Grid item xs={12} md={6}>
          <Paper sx={{ p: 2 }}>
            <Typography variant="h6" gutterBottom>
              System Status
            </Typography>
            <Divider sx={{ mb: 2 }} />
            <Box>
              <Typography variant="body1" color="textSecondary" gutterBottom>
                API Gateway: <span style={{ color: 'green' }}>● Running</span>
              </Typography>
              <Typography variant="body1" color="textSecondary" gutterBottom>
                University Service: <span style={{ color: 'green' }}>● Running</span>
              </Typography>
              <Typography variant="body1" color="textSecondary" gutterBottom>
                Course Service: <span style={{ color: 'green' }}>● Running</span>
              </Typography>
              <Typography variant="body1" color="textSecondary" gutterBottom>
                Service Registry: <span style={{ color: 'green' }}>● Running</span>
              </Typography>
            </Box>
          </Paper>
        </Grid>
      </Grid>
    </Box>
  );
};

export default Dashboard;
