import React, { useState, useEffect } from 'react';
import { universityAPI, courseAPI } from '../services/api';
import {
  Box, Grid, Card, CardContent, Typography, Container,
  LinearProgress, Avatar, Chip, Table, TableBody,
  TableCell, TableContainer, TableHead, TableRow,
  Paper, useTheme, alpha, Divider, IconButton
} from '@mui/material';
import {
  School, MenuBook, People, AttachMoney,
  TrendingUp, TrendingDown, CheckCircle,
  Error, Warning, Info, ArrowForward,
  Add, MoreVert, CalendarToday, LocationOn,
  Group, LibraryBooks, VerifiedUser
} from '@mui/icons-material';
import { LineChart, Line, BarChart, Bar, PieChart, Pie, Cell,
  XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer
} from 'recharts';

const Dashboard = () => {
  const theme = useTheme();
  const [stats, setStats] = useState({
    totalUniversities: 0,
    totalCourses: 0,
    activeCourses: 0,
    totalRevenue: 0,
    studentsEnrolled: 0,
    departments: 0
  });
  const [recentCourses, setRecentCourses] = useState([]);
  const [universities, setUniversities] = useState([]);
  const [loading, setLoading] = useState(true);
  const [chartData, setChartData] = useState([]);
  const [departmentData, setDepartmentData] = useState([]);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      
      // Fetch universities
      const universitiesRes = await universityAPI.getAllUniversities();
      const universitiesData = universitiesRes.data;
      
      // Fetch all courses
      const coursesRes = await courseAPI.getAllCourses();
      const coursesData = coursesRes.data;
      
      // Fetch active courses
      const activeCoursesRes = await courseAPI.getActiveCourses();
      const activeCoursesData = activeCoursesRes.data;
      
      // Calculate total revenue
      const totalRevenue = coursesData.reduce((sum, course) => 
        sum + (parseFloat(course.tuitionFee) || 0), 0
      );
      
      // Calculate unique departments
      const uniqueDepartments = [...new Set(coursesData.map(course => course.department))];
      
      // Prepare chart data
      const monthlyData = [
        { month: 'Jan', courses: 45, revenue: 45000 },
        { month: 'Feb', courses: 52, revenue: 52000 },
        { month: 'Mar', courses: 48, revenue: 48000 },
        { month: 'Apr', courses: 61, revenue: 61000 },
        { month: 'May', courses: 55, revenue: 55000 },
        { month: 'Jun', courses: 58, revenue: 58000 },
      ];
      
      // Prepare department distribution
      const departmentDistribution = uniqueDepartments.map(dept => ({
        name: dept,
        value: coursesData.filter(course => course.department === dept).length
      }));
      
      setStats({
        totalUniversities: universitiesData.length,
        totalCourses: coursesData.length,
        activeCourses: activeCoursesData.length,
        totalRevenue: totalRevenue,
        studentsEnrolled: Math.floor(coursesData.length * 35), // Estimated
        departments: uniqueDepartments.length
      });
      
      setUniversities(universitiesData.slice(0, 3));
      setRecentCourses(coursesData.slice(-5).reverse());
      setChartData(monthlyData);
      setDepartmentData(departmentDistribution.slice(0, 5));
      
    } catch (err) {
      console.error('Failed to fetch dashboard data', err);
    } finally {
      setLoading(false);
    }
  };

  const StatCard = ({ icon: Icon, title, value, change, color, bgColor }) => (
    <Card sx={{ 
      background: `linear-gradient(135deg, ${color}22 0%, ${color}11 100%)`,
      borderRadius: 3,
      boxShadow: '0 8px 32px rgba(0,0,0,0.08)',
      transition: 'transform 0.3s, box-shadow 0.3s',
      '&:hover': {
        transform: 'translateY(-5px)',
        boxShadow: '0 12px 48px rgba(0,0,0,0.12)'
      }
    }}>
      <CardContent>
        <Box display="flex" alignItems="center" justifyContent="space-between" mb={2}>
          <Avatar sx={{ 
            bgcolor: bgColor,
            width: 56,
            height: 56,
            boxShadow: `0 4px 12px ${color}40`
          }}>
            <Icon sx={{ fontSize: 28, color: color }} />
          </Avatar>
          <Box textAlign="right">
            {change && (
              <Chip 
                size="small"
                label={change}
                sx={{ 
                  bgcolor: change.includes('+') ? '#4caf5022' : '#f4433622',
                  color: change.includes('+') ? '#4caf50' : '#f44336',
                  fontWeight: 600
                }}
              />
            )}
          </Box>
        </Box>
        <Typography variant="h3" fontWeight="bold" sx={{ color }}>
          {title.includes('Revenue') ? `$${(value/1000).toFixed(1)}K` : value}
        </Typography>
        <Typography variant="body2" color="text.secondary" mt={1}>
          {title}
        </Typography>
        <Box display="flex" alignItems="center" mt={2}>
          {change && change.includes('+') ? (
            <TrendingUp sx={{ fontSize: 16, color: '#4caf50', mr: 0.5 }} />
          ) : change ? (
            <TrendingDown sx={{ fontSize: 16, color: '#f44336', mr: 0.5 }} />
          ) : null}
          <Typography variant="caption" color="text.secondary">
            {change ? 'Since last month' : 'Total count'}
          </Typography>
        </Box>
      </CardContent>
    </Card>
  );

  const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#8884D8'];

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="80vh">
        <Box textAlign="center">
          <LinearProgress sx={{ width: 300, height: 8, borderRadius: 4 }} />
          <Typography variant="h6" mt={2} color="text.secondary">
            Loading University Dashboard...
          </Typography>
        </Box>
      </Box>
    );
  }

  return (
    <Container maxWidth="xl" sx={{ py: 4 }}>
      {/* Header */}
      <Box sx={{ mb: 4 }}>
        <Typography variant="h3" fontWeight="bold" gutterBottom>
          University Management Dashboard
        </Typography>
        <Typography variant="body1" color="text.secondary">
          Welcome to your university management portal. Here's an overview of your institution's performance.
        </Typography>
      </Box>

      {/* Stats Cards */}
      <Grid container spacing={3} sx={{ mb: 4 }}>
        <Grid item xs={12} sm={6} md={4} lg={2}>
          <StatCard
            icon={School}
            title="Universities"
            value={stats.totalUniversities}
            change="+12%"
            color="#3f51b5"
            bgColor="#3f51b522"
          />
        </Grid>
        <Grid item xs={12} sm={6} md={4} lg={2}>
          <StatCard
            icon={MenuBook}
            title="Courses"
            value={stats.totalCourses}
            change="+8%"
            color="#f50057"
            bgColor="#f5005722"
          />
        </Grid>
        <Grid item xs={12} sm={6} md={4} lg={2}>
          <StatCard
            icon={People}
            title="Active Courses"
            value={stats.activeCourses}
            change="+15%"
            color="#4caf50"
            bgColor="#4caf5022"
          />
        </Grid>
        <Grid item xs={12} sm={6} md={4} lg={2}>
          <StatCard
            icon={AttachMoney}
            title="Total Revenue"
            value={stats.totalRevenue}
            change="+23%"
            color="#ff9800"
            bgColor="#ff980022"
          />
        </Grid>
        <Grid item xs={12} sm={6} md={4} lg={2}>
          <StatCard
            icon={Group}
            title="Students"
            value={stats.studentsEnrolled}
            change="+18%"
            color="#9c27b0"
            bgColor="#9c27b022"
          />
        </Grid>
        <Grid item xs={12} sm={6} md={4} lg={2}>
          <StatCard
            icon={LibraryBooks}
            title="Departments"
            value={stats.departments}
            change="+5%"
            color="#00bcd4"
            bgColor="#00bcd422"
          />
        </Grid>
      </Grid>

      {/* Charts Section */}
      <Grid container spacing={3} sx={{ mb: 4 }}>
        {/* Revenue Trend Chart */}
        <Grid item xs={12} md={8}>
          <Card sx={{ 
            borderRadius: 3,
            boxShadow: '0 8px 32px rgba(0,0,0,0.08)',
            p: 3
          }}>
            <Box display="flex" justifyContent="space-between" alignItems="center" mb={3}>
              <Box>
                <Typography variant="h6" fontWeight="bold">
                  Revenue & Course Growth
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Monthly performance overview
                </Typography>
              </Box>
              <Chip icon={<CalendarToday />} label="Last 6 months" variant="outlined" />
            </Box>
            <Box sx={{ height: 300 }}>
              <ResponsiveContainer width="100%" height="100%">
                <LineChart data={chartData}>
                  <CartesianGrid strokeDasharray="3 3" stroke={alpha(theme.palette.divider, 0.5)} />
                  <XAxis dataKey="month" />
                  <YAxis yAxisId="left" />
                  <YAxis yAxisId="right" orientation="right" />
                  <Tooltip 
                    contentStyle={{ 
                      borderRadius: 8,
                      border: 'none',
                      boxShadow: theme.shadows[3]
                    }}
                  />
                  <Legend />
                  <Line
                    yAxisId="left"
                    type="monotone"
                    dataKey="courses"
                    stroke="#3f51b5"
                    strokeWidth={3}
                    dot={{ r: 6 }}
                    activeDot={{ r: 8 }}
                    name="Courses"
                  />
                  <Line
                    yAxisId="right"
                    type="monotone"
                    dataKey="revenue"
                    stroke="#4caf50"
                    strokeWidth={3}
                    dot={{ r: 6 }}
                    activeDot={{ r: 8 }}
                    name="Revenue ($)"
                  />
                </LineChart>
              </ResponsiveContainer>
            </Box>
          </Card>
        </Grid>

        {/* Department Distribution */}
        <Grid item xs={12} md={4}>
          <Card sx={{ 
            borderRadius: 3,
            boxShadow: '0 8px 32px rgba(0,0,0,0.08)',
            p: 3,
            height: '100%'
          }}>
            <Typography variant="h6" fontWeight="bold" mb={3}>
              Department Distribution
            </Typography>
            <Box sx={{ height: 250 }}>
              <ResponsiveContainer width="100%" height="100%">
                <PieChart>
                  <Pie
                    data={departmentData}
                    cx="50%"
                    cy="50%"
                    labelLine={false}
                    label={({ name, percent }) => `${name}: ${(percent * 100).toFixed(0)}%`}
                    outerRadius={80}
                    fill="#8884d8"
                    dataKey="value"
                  >
                    {departmentData.map((entry, index) => (
                      <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                    ))}
                  </Pie>
                  <Tooltip />
                </PieChart>
              </ResponsiveContainer>
            </Box>
          </Card>
        </Grid>
      </Grid>

      {/* Recent Data Section */}
      <Grid container spacing={3}>
        {/* Recent Courses */}
        <Grid item xs={12} md={6}>
          <Card sx={{ 
            borderRadius: 3,
            boxShadow: '0 8px 32px rgba(0,0,0,0.08)',
            p: 3
          }}>
            <Box display="flex" justifyContent="space-between" alignItems="center" mb={3}>
              <Box>
                <Typography variant="h6" fontWeight="bold">
                  Recently Added Courses
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Latest courses in the system
                </Typography>
              </Box>
              <IconButton>
                <ArrowForward />
              </IconButton>
            </Box>
            <TableContainer>
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell>Course Code</TableCell>
                    <TableCell>Course Name</TableCell>
                    <TableCell>Department</TableCell>
                    <TableCell align="right">Fee</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {recentCourses.map((course) => (
                    <TableRow 
                      key={course.id}
                      hover
                      sx={{ 
                        '&:last-child td, &:last-child th': { border: 0 },
                        cursor: 'pointer',
                        transition: 'background-color 0.2s',
                        '&:hover': {
                          backgroundColor: alpha(theme.palette.primary.main, 0.04)
                        }
                      }}
                    >
                      <TableCell>
                        <Chip 
                          label={course.courseCode}
                          size="small"
                          sx={{ 
                            bgcolor: alpha(theme.palette.primary.main, 0.1),
                            color: theme.palette.primary.main,
                            fontWeight: 600
                          }}
                        />
                      </TableCell>
                      <TableCell>
                        <Typography variant="body2" fontWeight={500}>
                          {course.title}
                        </Typography>
                        <Typography variant="caption" color="text.secondary">
                          {course.instructor || 'No instructor'}
                        </Typography>
                      </TableCell>
                      <TableCell>
                        <Chip 
                          label={course.department}
                          size="small"
                          variant="outlined"
                        />
                      </TableCell>
                      <TableCell align="right">
                        <Typography variant="body2" fontWeight={600}>
                          ${parseFloat(course.tuitionFee || 0).toFixed(2)}
                        </Typography>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>
          </Card>
        </Grid>

        {/* University List & System Status */}
        <Grid item xs={12} md={6}>
          <Grid container spacing={3}>
            {/* Top Universities */}
            <Grid item xs={12}>
              <Card sx={{ 
                borderRadius: 3,
                boxShadow: '0 8px 32px rgba(0,0,0,0.08)',
                p: 3
              }}>
                <Typography variant="h6" fontWeight="bold" mb={3}>
                  Top Universities
                </Typography>
                <Grid container spacing={2}>
                  {universities.map((uni, index) => (
                    <Grid item xs={12} key={uni.id}>
                      <Box 
                        display="flex" 
                        alignItems="center" 
                        p={2}
                        sx={{
                          borderRadius: 2,
                          bgcolor: alpha(theme.palette.primary.main, 0.03),
                          border: `1px solid ${alpha(theme.palette.divider, 0.1)}`,
                          transition: 'all 0.2s',
                          '&:hover': {
                            bgcolor: alpha(theme.palette.primary.main, 0.08),
                            borderColor: alpha(theme.palette.primary.main, 0.3)
                          }
                        }}
                      >
                        <Avatar sx={{ 
                          bgcolor: COLORS[index % COLORS.length],
                          mr: 2
                        }}>
                          {uni.name.charAt(0)}
                        </Avatar>
                        <Box flex={1}>
                          <Typography variant="body1" fontWeight={600}>
                            {uni.name}
                          </Typography>
                          <Box display="flex" alignItems="center" mt={0.5}>
                            <LocationOn sx={{ fontSize: 14, mr: 0.5, color: 'text.secondary' }} />
                            <Typography variant="caption" color="text.secondary">
                              {uni.location}
                            </Typography>
                          </Box>
                        </Box>
                        <Chip 
                          label={`${Math.floor(Math.random() * 50) + 20} courses`}
                          size="small"
                          variant="outlined"
                        />
                      </Box>
                    </Grid>
                  ))}
                </Grid>
              </Card>
            </Grid>

            {/* System Status */}
            <Grid item xs={12}>
              <Card sx={{ 
                borderRadius: 3,
                boxShadow: '0 8px 32px rgba(0,0,0,0.08)',
                p: 3
              }}>
                <Typography variant="h6" fontWeight="bold" mb={3}>
                  System Status
                </Typography>
                <Grid container spacing={2}>
                  {[
                    { service: 'API Gateway', status: 'running', icon: VerifiedUser, color: '#4caf50' },
                    { service: 'University Service', status: 'running', icon: School, color: '#4caf50' },
                    { service: 'Course Service', status: 'running', icon: MenuBook, color: '#4caf50' },
                    { service: 'Service Registry', status: 'running', icon: CheckCircle, color: '#4caf50' },
                    { service: 'Database', status: 'connected', icon: VerifiedUser, color: '#4caf50' },
                    { service: 'Frontend', status: 'online', icon: VerifiedUser, color: '#4caf50' },
                  ].map((item, index) => (
                    <Grid item xs={6} key={index}>
                      <Box display="flex" alignItems="center">
                        <item.icon sx={{ 
                          color: item.color,
                          mr: 1.5,
                          fontSize: 20
                        }} />
                        <Box>
                          <Typography variant="body2" fontWeight={500}>
                            {item.service}
                          </Typography>
                          <Box display="flex" alignItems="center">
                            <Box sx={{
                              width: 8,
                              height: 8,
                              borderRadius: '50%',
                              bgcolor: item.color,
                              mr: 0.5
                            }} />
                            <Typography variant="caption" color="text.secondary">
                              {item.status}
                            </Typography>
                          </Box>
                        </Box>
                      </Box>
                    </Grid>
                  ))}
                </Grid>
              </Card>
            </Grid>
          </Grid>
        </Grid>
      </Grid>

      {/* Quick Actions */}
      <Box mt={4}>
        <Card sx={{ 
          borderRadius: 3,
          boxShadow: '0 8px 32px rgba(0,0,0,0.08)',
          p: 3,
          background: `linear-gradient(135deg, ${theme.palette.primary.main}08 0%, ${theme.palette.secondary.main}08 100%)`
        }}>
          <Box display="flex" justifyContent="space-between" alignItems="center">
            <Box>
              <Typography variant="h6" fontWeight="bold">
                Quick Actions
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Manage your university system efficiently
              </Typography>
            </Box>
            <Box display="flex" gap={2}>
              <IconButton sx={{ 
                bgcolor: theme.palette.primary.main,
                color: 'white',
                '&:hover': { bgcolor: theme.palette.primary.dark }
              }}>
                <Add />
              </IconButton>
              <IconButton sx={{ 
                bgcolor: alpha(theme.palette.primary.main, 0.1),
                color: theme.palette.primary.main
              }}>
                <MoreVert />
              </IconButton>
            </Box>
          </Box>
          <Grid container spacing={2} mt={2}>
            {[
              { label: 'Add New University', icon: School, color: '#3f51b5' },
              { label: 'Create Course', icon: MenuBook, color: '#f50057' },
              { label: 'Manage Students', icon: Group, color: '#4caf50' },
              { label: 'View Reports', icon: AttachMoney, color: '#ff9800' },
            ].map((action, index) => (
              <Grid item xs={12} sm={6} md={3} key={index}>
                <Box 
                  display="flex" 
                  alignItems="center" 
                  p={2}
                  sx={{
                    borderRadius: 2,
                    bgcolor: 'white',
                    border: `1px solid ${alpha(theme.palette.divider, 0.2)}`,
                    cursor: 'pointer',
                    transition: 'all 0.2s',
                    '&:hover': {
                      transform: 'translateY(-2px)',
                      boxShadow: theme.shadows[4],
                      borderColor: action.color
                    }
                  }}
                >
                  <Avatar sx={{ 
                    bgcolor: alpha(action.color, 0.1),
                    color: action.color,
                    mr: 2,
                    width: 40,
                    height: 40
                  }}>
                    <action.icon />
                  </Avatar>
                  <Typography variant="body2" fontWeight={500}>
                    {action.label}
                  </Typography>
                </Box>
              </Grid>
            ))}
          </Grid>
        </Card>
      </Box>
    </Container>
  );
};

export default Dashboard;
