import React, { useState, useEffect } from 'react';
import { courseAPI, universityAPI } from '../services/api';
import {
  Table, TableBody, TableCell, TableContainer, TableHead, TableRow,
  Paper, Button, IconButton, Typography, Box, Alert, CircularProgress,
  Dialog, DialogTitle, DialogContent, DialogActions, TextField,
  Select, MenuItem, FormControl, InputLabel
} from '@mui/material';
import { Edit, Delete, Add } from '@mui/icons-material';

const CourseList = () => {
  const [courses, setCourses] = useState([]);
  const [universities, setUniversities] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [openDialog, setOpenDialog] = useState(false);
  const [currentCourse, setCurrentCourse] = useState({
    courseCode: '',
    title: '',
    description: '',
    creditHours: '',
    department: '',
    semester: '',
    academicYear: '',
    instructor: '',
    prerequisites: '',
    tuitionFee: '',
    isActive: true,
    universityId: ''
  });
  const [editing, setEditing] = useState(false);

  useEffect(() => {
    fetchCourses();
    fetchUniversities();
  }, []);

  const fetchCourses = async () => {
    try {
      setLoading(true);
      const response = await courseAPI.getAllCourses();
      setCourses(response.data);
      setError('');
    } catch (err) {
      setError('Failed to fetch courses');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const fetchUniversities = async () => {
    try {
      const response = await universityAPI.getAllUniversities();
      setUniversities(response.data);
    } catch (err) {
      console.error('Failed to fetch universities', err);
    }
  };

  const handleOpenDialog = (course = null) => {
    if (course) {
      setCurrentCourse({
        ...course,
        tuitionFee: course.tuitionFee || '',
        universityId: course.universityId || ''
      });
      setEditing(true);
    } else {
      setCurrentCourse({
        courseCode: '',
        title: '',
        description: '',
        creditHours: '',
        department: '',
        semester: '',
        academicYear: '',
        instructor: '',
        prerequisites: '',
        tuitionFee: '',
        isActive: true,
        universityId: ''
      });
      setEditing(false);
    }
    setOpenDialog(true);
  };

  const handleCloseDialog = () => {
    setOpenDialog(false);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setCurrentCourse(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSave = async () => {
    try {
      const courseData = {
        ...currentCourse,
        creditHours: parseInt(currentCourse.creditHours) || 0,
        tuitionFee: currentCourse.tuitionFee ? parseFloat(currentCourse.tuitionFee) : null,
        universityId: currentCourse.universityId ? parseInt(currentCourse.universityId) : null
      };

      if (editing) {
        await courseAPI.updateCourse(currentCourse.id, courseData);
      } else {
        await courseAPI.createCourse(courseData);
      }
      fetchCourses();
      handleCloseDialog();
    } catch (err) {
      setError('Failed to save course');
      console.error(err);
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this course?')) {
      try {
        await courseAPI.deleteCourse(id);
        fetchCourses();
      } catch (err) {
        setError('Failed to delete course');
        console.error(err);
      }
    }
  };

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="200px">
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Box>
      <Box display="flex" justifyContent="space-between" alignItems="center" mb={3}>
        <Typography variant="h4">Courses</Typography>
        <Button
          variant="contained"
          color="primary"
          startIcon={<Add />}
          onClick={() => handleOpenDialog()}
        >
          Add Course
        </Button>
      </Box>

      {error && (
        <Alert severity="error" sx={{ mb: 2 }}>
          {error}
        </Alert>
      )}

      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Code</TableCell>
              <TableCell>Title</TableCell>
              <TableCell>Department</TableCell>
              <TableCell>Credit Hours</TableCell>
              <TableCell>Instructor</TableCell>
              <TableCell>Tuition Fee</TableCell>
              <TableCell>Actions</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {courses.map((course) => (
              <TableRow key={course.id}>
                <TableCell>{course.courseCode}</TableCell>
                <TableCell>{course.title}</TableCell>
                <TableCell>{course.department}</TableCell>
                <TableCell>{course.creditHours}</TableCell>
                <TableCell>{course.instructor}</TableCell>
                <TableCell>${course.tuitionFee || '0.00'}</TableCell>
                <TableCell>
                  <IconButton
                    color="primary"
                    onClick={() => handleOpenDialog(course)}
                  >
                    <Edit />
                  </IconButton>
                  <IconButton
                    color="error"
                    onClick={() => handleDelete(course.id)}
                  >
                    <Delete />
                  </IconButton>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>

      <Dialog open={openDialog} onClose={handleCloseDialog} maxWidth="sm" fullWidth>
        <DialogTitle>
          {editing ? 'Edit Course' : 'Add New Course'}
        </DialogTitle>
        <DialogContent>
          <Box component="form" sx={{ pt: 2 }}>
            <TextField
              fullWidth
              label="Course Code"
              name="courseCode"
              value={currentCourse.courseCode}
              onChange={handleInputChange}
              margin="normal"
              required
            />
            <TextField
              fullWidth
              label="Title"
              name="title"
              value={currentCourse.title}
              onChange={handleInputChange}
              margin="normal"
              required
            />
            <TextField
              fullWidth
              label="Description"
              name="description"
              value={currentCourse.description || ''}
              onChange={handleInputChange}
              margin="normal"
              multiline
              rows={3}
            />
            <TextField
              fullWidth
              label="Credit Hours"
              name="creditHours"
              type="number"
              value={currentCourse.creditHours}
              onChange={handleInputChange}
              margin="normal"
              required
            />
            <TextField
              fullWidth
              label="Department"
              name="department"
              value={currentCourse.department}
              onChange={handleInputChange}
              margin="normal"
              required
            />
            <TextField
              fullWidth
              label="Semester"
              name="semester"
              value={currentCourse.semester}
              onChange={handleInputChange}
              margin="normal"
              required
            />
            <TextField
              fullWidth
              label="Academic Year"
              name="academicYear"
              value={currentCourse.academicYear}
              onChange={handleInputChange}
              margin="normal"
              required
            />
            <TextField
              fullWidth
              label="Instructor"
              name="instructor"
              value={currentCourse.instructor || ''}
              onChange={handleInputChange}
              margin="normal"
            />
            <TextField
              fullWidth
              label="Prerequisites"
              name="prerequisites"
              value={currentCourse.prerequisites || ''}
              onChange={handleInputChange}
              margin="normal"
            />
            <TextField
              fullWidth
              label="Tuition Fee"
              name="tuitionFee"
              type="number"
              value={currentCourse.tuitionFee}
              onChange={handleInputChange}
              margin="normal"
              inputProps={{ step: "0.01" }}
            />
            <FormControl fullWidth margin="normal">
              <InputLabel>University</InputLabel>
              <Select
                name="universityId"
                value={currentCourse.universityId || ''}
                onChange={handleInputChange}
                label="University"
              >
                <MenuItem value="">Select University</MenuItem>
                {universities.map((uni) => (
                  <MenuItem key={uni.id} value={uni.id}>
                    {uni.name}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseDialog}>Cancel</Button>
          <Button onClick={handleSave} variant="contained" color="primary">
            Save
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default CourseList;
