import React, { useState, useEffect } from 'react';
import { universityAPI } from '../services/api';
import {
  Table, TableBody, TableCell, TableContainer, TableHead, TableRow,
  Paper, Button, IconButton, Typography, Box, Alert, CircularProgress,
  Dialog, DialogTitle, DialogContent, DialogActions, TextField
} from '@mui/material';
import { Edit, Delete, Add } from '@mui/icons-material';

const UniversityList = () => {
  const [universities, setUniversities] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [openDialog, setOpenDialog] = useState(false);
  const [currentUniversity, setCurrentUniversity] = useState({
    name: '',
    location: '',
    website: '',
    contactEmail: '',
    contactPhone: '',
    establishedYear: '',
    departments: [],
    faculties: []
  });
  const [editing, setEditing] = useState(false);

  useEffect(() => {
    fetchUniversities();
  }, []);

  const fetchUniversities = async () => {
    try {
      setLoading(true);
      const response = await universityAPI.getAllUniversities();
      setUniversities(response.data);
      setError('');
    } catch (err) {
      setError('Failed to fetch universities');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handleOpenDialog = (university = null) => {
    if (university) {
      setCurrentUniversity(university);
      setEditing(true);
    } else {
      setCurrentUniversity({
        name: '',
        location: '',
        website: '',
        contactEmail: '',
        contactPhone: '',
        establishedYear: '',
        departments: [],
        faculties: []
      });
      setEditing(false);
    }
    setOpenDialog(true);
  };

  const handleCloseDialog = () => {
    setOpenDialog(false);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setCurrentUniversity(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSave = async () => {
    try {
      if (editing) {
        await universityAPI.updateUniversity(currentUniversity.id, currentUniversity);
      } else {
        await universityAPI.createUniversity(currentUniversity);
      }
      fetchUniversities();
      handleCloseDialog();
    } catch (err) {
      setError('Failed to save university');
      console.error(err);
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this university?')) {
      try {
        await universityAPI.deleteUniversity(id);
        fetchUniversities();
      } catch (err) {
        setError('Failed to delete university');
        console.error(err);
      }
    }
  };

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="200px">
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Box>
      <Box display="flex" justifyContent="space-between" alignItems="center" mb={3}>
        <Typography variant="h4">Universities</Typography>
        <Button
          variant="contained"
          color="primary"
          startIcon={<Add />}
          onClick={() => handleOpenDialog()}
        >
          Add University
        </Button>
      </Box>

      {error && (
        <Alert severity="error" sx={{ mb: 2 }}>
          {error}
        </Alert>
      )}

      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>ID</TableCell>
              <TableCell>Name</TableCell>
              <TableCell>Location</TableCell>
              <TableCell>Contact Email</TableCell>
              <TableCell>Contact Phone</TableCell>
              <TableCell>Actions</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {universities.map((university) => (
              <TableRow key={university.id}>
                <TableCell>{university.id}</TableCell>
                <TableCell>{university.name}</TableCell>
                <TableCell>{university.location}</TableCell>
                <TableCell>{university.contactEmail}</TableCell>
                <TableCell>{university.contactPhone}</TableCell>
                <TableCell>
                  <IconButton
                    color="primary"
                    onClick={() => handleOpenDialog(university)}
                  >
                    <Edit />
                  </IconButton>
                  <IconButton
                    color="error"
                    onClick={() => handleDelete(university.id)}
                  >
                    <Delete />
                  </IconButton>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>

      <Dialog open={openDialog} onClose={handleCloseDialog} maxWidth="sm" fullWidth>
        <DialogTitle>
          {editing ? 'Edit University' : 'Add New University'}
        </DialogTitle>
        <DialogContent>
          <Box component="form" sx={{ pt: 2 }}>
            <TextField
              fullWidth
              label="Name"
              name="name"
              value={currentUniversity.name}
              onChange={handleInputChange}
              margin="normal"
              required
            />
            <TextField
              fullWidth
              label="Location"
              name="location"
              value={currentUniversity.location}
              onChange={handleInputChange}
              margin="normal"
              required
            />
            <TextField
              fullWidth
              label="Website"
              name="website"
              value={currentUniversity.website || ''}
              onChange={handleInputChange}
              margin="normal"
            />
            <TextField
              fullWidth
              label="Contact Email"
              name="contactEmail"
              type="email"
              value={currentUniversity.contactEmail || ''}
              onChange={handleInputChange}
              margin="normal"
            />
            <TextField
              fullWidth
              label="Contact Phone"
              name="contactPhone"
              value={currentUniversity.contactPhone || ''}
              onChange={handleInputChange}
              margin="normal"
            />
            <TextField
              fullWidth
              label="Established Year"
              name="establishedYear"
              type="number"
              value={currentUniversity.establishedYear || ''}
              onChange={handleInputChange}
              margin="normal"
            />
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseDialog}>Cancel</Button>
          <Button onClick={handleSave} variant="contained" color="primary">
            Save
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default UniversityList;
